import typesclass Person(object):    def __init__(self, name):        self.name = name    def run(self):        print('run----')    def __getattribute__(self, item):        temp = object.__getattribute__(self, item)        print(temp)        return tempdef sport(self):    print('%s is sport' % self.name)@classmethoddef cla_method(cls):    print(cls.car)@staticmethoddef sta_method():    print('static method')p = Person('yeason')p.sport = types.MethodType(sport, p)p.sport()# 添加类方法到类# Person.car = 'BMW'# Person.cla_method = cla_method# Person.cla_method(type(Person))# 添加静态方法到类# Person.sta_method = sta_method# Person.sta_method()